import React, {useState} from 'react'
import Map from "./Map"
import  Search  from './Search'
import {Button} from 'semantic-ui-react'

const KakaoMap = () => {

  const [search, setSearch] = useState('');
  const [searchArr, setSearchArr] = useState([]); 
  const [count , setCount] = useState(0);
  const addSearch = () =>{
    let newArr = [...searchArr];
    newArr.push(search);
    setSearchArr(newArr);
    setSearch('');
  }
  const deleteBtn =(index)=>{
    let deleteTarget = searchArr.length ;
    searchArr.pop(deleteTarget);
    console.log(deleteTarget);

  }
  const plus =()=>{
    setCount + 1 ;
  }
  return (
    <div style={{display: "flex", flexDirection: "row", marginTop:"20px", justifyContent:"space-around" }}>
      <Map />
      <div style={{width: '100%', display: 'flex', flexDirection: 'column'}}>
        <Search onClick={addSearch} value={search} setSearch={setSearch} />
        <div style={{border:"1px solid black" , display:"flex", justifyContent:"center" ,flexDirection:"column"}}>
        {searchArr.map((sec) => (
          <p id={searchArr.length -1 }>{sec}<Button onClick={deleteBtn(searchArr.length -1)}>삭제</Button></p>

        ))} 
          <div>{count}</div>
          <button type="buttton" onClick={plus}>업</button>
        </div>
      </div>
    </div>
  )
}

export default KakaoMap
