    newArr.push(search);
    setSearchArr(newArr);
    setSearch('');
    console.log(searchArr) ; 


  return (
    <div>
      <Foot onClick={add} value={search} setSearch={setSearch}/>
      <div>{
        searchArr.map((sec)=>
      <p>{sec}</p>)
      }</div>
    </div>
  )
}
}

export default Top ;
